import React, { Component } from 'react'

class SearchBar extends Component {  
    
    constructor(props) {
        super(props);
        this.State = { movies: [], loaded: false };
    }    

    handleInputChange = () => {
        if (this.search.value.length>=3) {
            this.populateAutoComplete();
        }                
    }

    render() {
        let contents = this.State.loaded
            ? this.State.movies(movie => ( <p>{movie.Original_title}</p> ) )
            :  < p > No movies </p>

        return (
            <form>
                <input
                    placeholder="Search for..."
                    ref={input => this.search = input}
                    onChange={this.handleInputChange}
                    autoComplete="on"
                />
                {contents}             
            </form>
        )
    }

    async populateAutoComplete() {
        const response = await fetch('MovieDb?' + new URLSearchParams({ query: this.search.value }));
        const data = await response.json();    
        this.setState({
            movies: data.results,
            loaded: true
        })
    }
}



export default SearchBar;
